  const getColor = val => val > 80 ? '#f44336' : val > 60 ? '#ffeb3b' : '#00e676';

  // Retorna classe de anima√ß√£o de alerta se o valor for alto
  const getBlinkClass = val => val > 60 ? 'pulse' : '';

  // Converte uptime (em segundos) para dias, horas e minutos
  const formatUptime = seconds => {
    if (!seconds || isNaN(seconds)) return '-';
    const d = Math.floor(seconds / (3600 * 24));
    const h = Math.floor((seconds % (3600 * 24)) / 3600);
    const m = Math.floor((seconds % 3600) / 60);
    return `${d}d ${h}h ${m}m`;
  };

  // Garante que o valor percentual fique entre 0 e 100
  const safePercent = val => {
    const num = parseFloat(val);
    if (isNaN(num)) return 0;
    return Math.min(Math.max(num, 0), 100);
  };

  // Agrupa os dados dos hosts
  const hostsData = groupByHost();
  let rowsHtml = '';

  // Itera por cada host e gera a linha de dados correspondente
  for (const [host, seriesList] of Object.entries(hostsData)) {
    const systemName = getLastValue(seriesList, 'Host name', 'string') ?? host;
    const cpuRaw = getLastValue(seriesList, 'CPU utilization') ?? 0;
    const memoryRaw = getLastValue(seriesList, 'Memory utilization') ?? 0;
    const memoryTotal = getLastValue(seriesList, 'Total memory') ?? 1;
    const cores = getLastValue(seriesList, 'Number of CPUs') ?? '-';
    const statusRaw = getLastValue(seriesList, 'Status') ?? 0;
    const uptimeRaw = getLastValue(seriesList, 'System uptime') ?? 0;

    // Formata os valores e define status e estilo
    const cpu = safePercent(cpuRaw);
    const memoryPct = safePercent(memoryRaw);
    const uptimeText = formatUptime(uptimeRaw);
    const isStopped = statusRaw === 1;
    const statusText = isStopped ? 'Stopped' : 'Running';
    const statusColorClass = isStopped ? 'status-stopped' : 'status-online';
    const isBlinking = cpu > 60 || memoryPct > 60;

    // Cria o HTML da linha do host
    rowsHtml += `
      <div class="card-row ${isBlinking ? 'pulse' : ''}">
        <div><div class="value">${systemName}</div></div>
        <div><div class="value ${statusColorClass}">${statusText}</div></div>
        <div><div class="value">${cores}</div></div>
        <div>
          <div class="value">${formatPct(cpu)}</div>
          <div class="bar-container">
            <div class="bar ${getBlinkClass(cpu)}" style="width:${cpu.toFixed(0)}%; background-color: ${getColor(cpu)};"></div>
          </div>
        </div>
        <div><div class="value">${formatGB(memoryTotal)}</div></div>
        <div>
          <div class="value">${formatPct(memoryPct)}</div>
          <div class="bar-container">
            <div class="bar ${getBlinkClass(memoryPct)}" style="width:${memoryPct.toFixed(0)}%; background-color: ${getColor(memoryPct)};"></div>
          </div>
        </div>
        <div><div class="value">${uptimeText}</div></div>
      </div>
    `;
  }

  // Define o HTML e o CSS final do painel
  htmlNode.innerHTML = `
    <style>
      html, body, .panel-container {
        overflow: hidden !important;
      }

      .card-wrapper {
        font-family: "Inter", sans-serif;
        background-color: #15171c;
        color: #fff;
        border-radius: 6px;
        width: 100%;
        height: 100%;
        overflow: hidden;
        display: flex;
        flex-direction: column;
      }

      .card-header {
        display: grid;
        grid-template-columns: repeat(7, 1fr);
        background: #1d1f24;
        color: #aaa;
        font-weight: 600;
        font-size: 13px;
        text-transform: uppercase;
        padding: 12px 0;
        text-align: center;
        position: sticky;
        top: 0;
        z-index: 999;
      }

      .card-body {
        overflow-y: auto;
        flex: 1;
      }

      .card-row {
        display: grid;
        grid-template-columns: repeat(7, 1fr);
        padding: 14px 0;
        text-align: center;
        align-items: center;
        border-top: 1px solid #2a2a2a;
        background: #15171c;
      }

      .value {
        font-weight: 500;
        font-size: 13px;
        margin-bottom: 4px;
        color: #f0f0f0;
      }

      .status-online {
        color: #00e676;
        font-weight: bold;
      }

      .status-stopped {
        color: #f44336;
        font-weight: bold;
      }

      .bar-container {
        background: #3a3a3a;
        border-radius: 6px;
        height: 8px;
        width: 75%;
        margin: 0 auto;
        overflow: hidden;
      }

      .bar {
        height: 100%;
        border-radius: 6px;
        transition: width 0.4s ease;
      }

      .pulse {
        animation: blink 1s infinite;
      }

      @keyframes blink {
        0%, 100% { background-color: #15171c; }
        50% { background-color: #2a2a2a; }
      }

      .bar.pulse {
        animation: blinkBorder 1s infinite;
      }

      @keyframes blinkBorder {
        0%, 100% { opacity: 1; }
        50% { opacity: 0.7; }
      }
    </style>

    <div class="card-wrapper">
      <div class="card-header">
        <div>NODE</div>
        <div>STATUS</div>
        <div>CORES</div>
        <div>CPU (%)</div>
        <div>TOTAL MEM</div>
        <div>MEMORY (%)</div>
        <div>UPTIME</div>
      </div>
      <div class="card-body">
        ${rowsHtml}
      </div>
    </div>
  `;
})();
